let data=[];$.get("/page-contents.json").then(response=>data=response);$("input#search").on("keyup",e=>{search()});$("input#search").on("blur",e=>{search()});function search(){const value=$("input#search").val();if(data===[]){$("input#search").hide();return}if(value===""){$("*").removeClass("darken");return}const result=data.filter(item=>{return item.keywords.some(keyword=>keyword.includes(value))});result.forEach(item=>{const parentElement=$(`#${item.parentElementId}`);$(`#content :not('#${item.parentElementId}')`).addClass("darken");$(`#${item.parentElementId} *`).removeClass("darken");parentElement.removeClass("darken");parentElement.parents().removeClass("darken")})}